import React, { useState } from 'react';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const pindahHalaman = (url) => {
    window.location.href = url;
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="bg-teal-100 fixed w-full z-10">
      {/* Tombol Hamburger untuk mobile */}
      <div className="sm:hidden p-5">
        <button onClick={toggleMenu} className="text-teal-800 text-3xl">
          â˜°
        </button>
      </div>

      {/* Sidebar untuk Desktop dan Mobile */}
      <div className={`fixed top-0 left-0 w-64 h-full bg-teal-100 p-5 transform transition-transform ${menuOpen ? 'translate-x-0' : '-translate-x-full'} sm:translate-x-0`}>
        {/* Logo */}
        <div className="text-teal-800 text-lg font-bold mb-8">Logo</div>

        {/* Menu Navigasi */}
        <ul className="space-y-6">
          <li><a href="/" className="text-teal-800 text-lg">Home</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/Video" className="text-teal-800 text-lg">Video</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/Info" className="text-teal-800 text-lg">Info</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/chat" className="text-teal-800 text-lg">Bantuan</a></li>
        </ul>

        {/* Tombol Login */}
        <button 
          className="bg-teal-800 text-white py-2 px-6 rounded-full text-lg hover:bg-teal-700 transition duration-300 mt-8"
          onClick={() => pindahHalaman('/login')}
        >
          Login
        </button>
      </div>

      {/* Konten utama di sebelah kanan */}
      <div className="ml-64 p-5">
        <nav className="flex justify-between items-center">
          <button 
            className="bg-teal-800 text-white py-2 px-6 rounded-full text-lg hover:bg-teal-700 transition duration-300"
            onClick={() => pindahHalaman('/login')}
          >
            Login
          </button>
        </nav>
      </div>
    </header>
  );
}

export default Header;
