import React, { useState, useEffect } from "react";
import axios from "axios";

function Carousel() {
  const [sliderData, setSliderData] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);

  const goToSlide = (index) => {
    if (index >= sliderData.length) {
      setActiveIndex(0);
    } else if (index < 0) {
      setActiveIndex(sliderData.length - 1);
    } else {
      setActiveIndex(index);
    }
  };

  const nextSlide = () => {
    setActiveIndex((prevIndex) => {
      return prevIndex + 1 >= sliderData.length ? 0 : prevIndex + 1;
    });
  };

  useEffect(() => {
    axios
      .get("http://localhost:3002/slider")
      .then((response) => {
        setSliderData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching slider data:", error);
      });

    const interval = setInterval(nextSlide, 5000); // Set interval to go to next slide every 5 seconds

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, [sliderData.length]); // Empty array dependency to trigger once on data load

  return (
    <div className="carousel relative max-w-full overflow-hidden mx-auto pt-16">
      {/* Carousel Inner */}
      <div
        className="carousel-inner flex transition-transform duration-500 ease-in-out"
        style={{ transform: `translateX(-${activeIndex * 100}%)` }}
      >
        {sliderData.map((slide, index) => (
          <div
            key={slide.id}
            className="carousel-item w-full flex-none"
          >
            <img
              src={slide.source}
              alt={`Slide ${index + 1}`}
              className="w-full h-[300px] object-cover md:h-[500px] lg:h-[600px]"
            />
          </div>
        ))}
      </div>

      {/* Carousel Indicators */}
      <div className="carousel-indicators absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
        {sliderData.map((_, index) => (
          <span
            key={index}
            className={`indicator w-2.5 h-2.5 rounded-full cursor-pointer ${
              activeIndex === index ? "bg-teal-500" : "bg-white"
            }`}
            onClick={() => goToSlide(index)}
          ></span>
        ))}
      </div>

      {/* Navigation Buttons */}
      <button
        className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white rounded-full p-2 focus:outline-none hover:bg-opacity-75"
        onClick={() => goToSlide(activeIndex - 1)}
      >
        &lt;
      </button>
      <button
        className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white rounded-full p-2 focus:outline-none hover:bg-opacity-75"
        onClick={() => goToSlide(activeIndex + 1)}
      >
        &gt;
      </button>
    </div>
  );
}

export default Carousel;
