import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function Chat() {
  const [messages, setMessages] = useState([
    { text: 'Hello there?', time: '11:42', sender: 'left' },
    { text: 'Hey! How can we help you today?', time: '11:45', sender: 'right' },
  ]);

  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() === '') return;

    const currentTime = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    setMessages([
      ...messages,
      { text: newMessage, time: currentTime, sender: 'right' },
    ]);

    setNewMessage('');
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
  };

  return (
    <div className="App">
      <Header />
      <div className="sela"></div>
      <div className="flex flex-col h-full p-6 space-y-6">
        <div className="flex-1 bg-teal-100 p-6 rounded-lg shadow-lg overflow-y-auto max-h-[500px]">
          {messages.map((message, index) => (
            <div key={index} className={`flex flex-col mb-4 max-w-[30%] animate-fadeIn ${message.sender === 'left' ? 'items-start bg-teal-200 rounded-l-lg' : 'items-end bg-teal-400 rounded-r-lg'}`}>
              <div className="text-sm mb-1">{message.text}</div>
              <div className="text-xs text-right text-gray-600">{message.time}</div>
            </div>
          ))}
        </div>
        <div className="flex items-center bg-teal-50 p-3 rounded-lg">
          <input
            type="text"
            className="flex-1 p-3 border-none rounded-lg focus:outline-none"
            placeholder="Type a message"
            value={newMessage}
            onChange={handleInputChange}
          />
          <button
            className="ml-3 bg-teal-700 text-white py-2 px-6 rounded-lg hover:bg-teal-800"
            onClick={handleSendMessage}
          >
            Send
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Chat;
