import React, { useState, useEffect } from 'react';
import api from './api'; // Mengimpor API untuk berkomunikasi dengan backend
import Header from './components/Header';
import Footer from './components/Footer';

function Chat() {
  const [messages, setMessages] = useState([]); // Menyimpan pesan yang diterima
  const [newMessage, setNewMessage] = useState(''); // Menyimpan pesan yang akan dikirim
  const [isLoading, setIsLoading] = useState(false); // Menyimpan status loading
  const [retryCount, setRetryCount] = useState(0); // Menyimpan jumlah percobaan ulang

  // Ambil pesan dari backend
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await api.get('/get-messages');
        setMessages(response.data.reverse()); // Membalikkan urutan pesan sehingga yang terbaru di bawah
        setRetryCount(0); // Reset retry count setelah berhasil mengambil pesan
      } catch (error) {
        if (error.response && error.response.status === 429) {
          // Menunggu sebelum mencoba ulang
          console.log('Rate limit tercapai, mencoba lagi...');
          if (retryCount < 3) {
            setRetryCount(retryCount + 1);
            setTimeout(fetchMessages, 1000); // Coba lagi setelah 2 detik
          } else {
            alert('Terlalu banyak permintaan, silakan refersh halaman');
          }
        } else {
          console.error('Error mengambil pesan:', error);
        }
      }
    };

    fetchMessages();
    const interval = setInterval(fetchMessages, 1000); // Mengambil pesan setiap 5 detik, bukan setiap detik
  
    return () => clearInterval(interval); // Bersihkan interval saat komponen di-unmount
  }, [retryCount]);

  // Kirim pesan ke backend
  const handleSendMessage = async () => {
    if (newMessage.trim() === '') return;

    // Ambil userSession dari localStorage
    const userSession = sessionStorage.getItem('userSession');
    if (!userSession) {
      alert('Session user tidak ditemukan!');
      return;
    }

    const currentTime = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    setIsLoading(true); // Mengatur status loading

    try {
      const response = await api.post('/send-messages', {
        message: newMessage,
        sender: userSession, // Menggunakan session user sebagai pengirim
      });

      setMessages((prevMessages) => [...prevMessages, { ...response.data, time: currentTime }]); // Menambahkan pesan di bawah
      setNewMessage('');
    } catch (error) {
      if (error.response && error.response.status === 429) {
        console.log('Rate limit tercapai saat mengirim pesan, mencoba lagi...');
        setTimeout(handleSendMessage, 2000); // Coba lagi setelah 2 detik
      } else {
        console.error('Error mengirim pesan:', error);
      }
    } finally {
      setIsLoading(false); // Mengatur status loading kembali
    }
  };

  // Mengatur perubahan input untuk pesan
  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
  };

  return (
    <div className="App">
      <Header />
      <div className="chat-container pt-40 flex flex-col justify-between p-6">
        <div className="chat-box flex-1 max-h-[500px] bg-teal-100 p-5 rounded-lg overflow-y-auto">
          {messages.reverse().map((message, index) => {
            const time = new Date(message.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

            return (
              <div
                key={index}
                className={`message mb-4 flex flex-col max-w-[30%] animate-fadeIn ${
                  message.sender === 'admin'
                    ? 'cr self-end bg-teal-500 text-white rounded-tl-lg rounded-bl-lg rounded-tr-lg'
                    : 'self-start bg-teal-200 rounded-tl-lg rounded-br-lg rounded-tr-lg'
                }`}
              >
                <div className="message-content m-5 mb-1 text-sm">{message.message}</div>
                <div className="message-time mr-3 mb-2 text-xs text-right text-gray-900 ">{time}</div>
              </div>
            );
          })}
        </div>
        <div className="chat-input flex items-center bg-teal-50 p-3 rounded-xl mt-4">
          <input
            type="text"
            placeholder="Type a message"
            value={newMessage}
            onChange={handleInputChange}
            className="flex-1 p-3 rounded-xl border-none focus:outline-none"
          />
          <button
            type="submit"
            className="send-btn bg-teal-700 text-white p-3 ml-3 rounded-lg cursor-pointer"
            onClick={handleSendMessage}
            disabled={isLoading} // Menonaktifkan tombol saat pesan sedang dikirim
          >
            {isLoading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Chat;
