import React, { useState } from 'react';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Footer from './components/Footer';

function Video() {
  const [selectedVideo, setSelectedVideo] = useState({
    title: 'Judul Utama',
    description: 'Deskripsi video utama',
    videoUrl: 'https://www.youtube.com/embed/dQw4w9WgXcQ', // Default video URL (YouTube)
  });

  const videos = [
    { title: 'Video 1', videoUrl: 'https://www.youtube.com/embed/dQw4w9WgXcQ' },
    { title: 'Video 2', videoUrl: 'https://www.youtube.com/embed/tgbNymZ7vqY' },
    { title: 'Video 3', videoUrl: 'https://www.youtube.com/embed/sBws8MSXN7A' },
    // TikTok videos
    { title: 'TikTok 1', videoUrl: 'https://www.tiktok.com/embed/1732975528245' },
    { title: 'TikTok 2', videoUrl: 'https://www.tiktok.com/embed/7198280763469111809' },
  ];

  const handleVideoClick = (video) => {
    setSelectedVideo({
      title: video.title,
      description: 'Deskripsi untuk ' + video.title,
      videoUrl: video.videoUrl,
    });
  };

  return (
    <div className="App">
      <Header />
      <Carousel />
      <section className="video-section">
        <h1>VIDEO</h1>
        <div className="video-grid"> {/* untuk video tiktok */ }
          {videos.map((video, index) => (
            <div className="video-card" key={index} onClick={() => handleVideoClick(video)}>
              <div className="video-thumbnail">
              <iframe src="https://www.tiktok.com/embed/7198274773733775622" width="325" height="576" frameborder="0"></iframe>

                <button className="play-button">â–¶</button>
              </div>
              <p>{video.title}</p>
            </div>
          ))}
        </div>

        <div className="large-video-card"> {/* untuk video youtube atau tiktok */ }
          <div className="video-thumbnail">
            <iframe
              width="560"
              height="315"
              src={selectedVideo.videoUrl}
              title={selectedVideo.title}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
          <p>{selectedVideo.title}</p>
          <p className="description">{selectedVideo.description}</p>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default Video;
