import React, { useState } from 'react';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Footer from './components/Footer';

function Video() {
  const [selectedVideo, setSelectedVideo] = useState({
    title: 'Judul Utama',
    description: 'Deskripsi video utama',
    tiktokUrl: 'https://www.tiktok.com/@username/video/1234567890123456789', // Example TikTok video URL
  });

  const videos = [
    { title: 'Video 1', tiktokUrl: 'https://www.tiktok.com/@username/video/1234567890123456789' },
    { title: 'Video 2', tiktokUrl: 'https://www.tiktok.com/@username/video/9876543210987654321' },
    { title: 'Video 3', tiktokUrl: 'https://www.tiktok.com/@username/video/1234987654321098765' },
  ];

  const handleVideoClick = (video) => {
    setSelectedVideo({
      title: video.title,
      description: 'Deskripsi untuk ' + video.title,
      tiktokUrl: video.tiktokUrl,
    });
  };

  return (
    <div className="App">
      <Header />
      <Carousel />
      <section className="video-section">
        <h1>VIDEO</h1>
        <div className="video-grid">
          {videos.map((video, index) => (
            <div className="video-card" key={index} onClick={() => handleVideoClick(video)}>
              <div className="video-thumbnail">
                <button className="play-button">▶</button>
              </div>
              <p>{video.title}</p>
            </div>
          ))}
        </div>

        <div className="large-video-card">
          <div className="video-thumbnail">
            <button className="play-button">▶</button>
          </div>
          <p>{selectedVideo.title}</p>
          <p className="description">{selectedVideo.description}</p>
          
          {/* TikTok Video Embed */}
          <div className="tiktok-embed">
            <iframe
              src={selectedVideo.tiktokUrl}
              width="100%"
              height="500px"
              frameBorder="0"
              allowFullScreen
              title={selectedVideo.title}
            ></iframe>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default Video;
