import React, { useState } from 'react';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const pindahHalaman = (url) => {
    window.location.href = url;
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="bg-teal-100 p-5 fixed w-full z-10">
      <nav className="flex justify-between items-center">
        {/* Logo atau Nama */}
        <img src="/images/v2_59.png" alt="Logo Kampus" className="w-18 mr-6" />

        {/* Menu Hamburger untuk mobile */}
        <div className="sm:hidden">
          <button onClick={toggleMenu} className="text-teal-800 text-3xl">
            â˜°
          </button>
        </div>

        {/* Menu navigasi (untuk layar besar) */}
        <ul className={`flex sm:flex-row sm:space-x-4 flex-col sm:flex-nowrap space-y-4 sm:space-y-0 absolute sm:relative bg-teal-100 sm:bg-transparent sm:top-0 sm:right-auto top-16 right-0 sm:flex`}>
          <li><a href="/" className="text-teal-800 text-lg">Home</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/Video" className="text-teal-800 text-lg">Video</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/Info" className="text-teal-800 text-lg">Info</a></li>
          <li><a href="#" className="text-teal-800 text-lg">|</a></li>
          <li><a href="/chat" className="text-teal-800 text-lg">Bantuan</a></li>
        </ul>

        {/* Tombol Login */}
        <button 
          className="bg-teal-800 text-white py-2 px-6 rounded-full text-lg hover:bg-teal-700 transition duration-300"
          onClick={() => pindahHalaman('/login')}
        >
          Login
        </button>
      </nav>

      {/* Menu Toggle untuk mobile */}
      {menuOpen && (
        <div className="sm:hidden bg-teal-100 p-4 flex flex-col items-center space-y-4">
          <ul className="space-y-4">
            <li><a href="/" className="text-teal-800 text-lg">Home</a></li>
            <li><a href="/Video" className="text-teal-800 text-lg">Video</a></li>
            <li><a href="/Info" className="text-teal-800 text-lg">Info</a></li>
            <li><a href="/chat" className="text-teal-800 text-lg">Bantuan</a></li>
          </ul>
          <button 
            className="bg-teal-800 text-white py-2 px-6 rounded-full text-lg hover:bg-teal-700 transition duration-300 mt-4"
            onClick={() => pindahHalaman('/login')}
          >
            Login
          </button>
        </div>
      )}
    </header>
  );
}

export default Header;
