import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function Chat() {
  const [messages, setMessages] = useState([
    { text: 'Hello there?', time: '11:42', sender: 'left' },
    { text: 'Hey! How can we help you today?', time: '11:45', sender: 'right' },
  ]);

  const [newMessage, setNewMessage] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim() === '') return;

    const currentTime = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    setMessages([
      ...messages,
      { text: newMessage, time: currentTime, sender: 'right' },
    ]);

    setNewMessage('');
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
  };

  return (
    <div className="App">
      <Header />
      <div className="sela"></div>
      <div className="flex flex-col justify-between p-5 h-screen">
        {/* Chat Box */}
        <div className="flex-1 max-h-[500px] bg-teal-100 p-4 rounded-lg overflow-y-auto">
          {messages.map((message, index) => (
            <div key={index} className={`mb-4 ${message.sender === 'left' ? 'items-start' : 'items-end'} flex flex-col`}>
              <div
                className={`max-w-[30%] px-4 py-2 rounded-xl text-sm ${
                  message.sender === 'left'
                    ? 'bg-teal-200 rounded-br-none'
                    : 'bg-teal-500 text-white rounded-bl-none'
                }`}
              >
                {message.text}
              </div>
              <div className="text-xs text-gray-500 text-right">{message.time}</div>
            </div>
          ))}
        </div>

        {/* Input Section */}
        <div className="flex items-center bg-teal-50 p-3 rounded-lg mt-4">
          <input
            type="text"
            placeholder="Type a message"
            value={newMessage}
            onChange={handleInputChange}
            className="flex-1 p-3 border-none rounded-lg focus:outline-none"
          />
          <button
            onClick={handleSendMessage}
            className="ml-3 bg-teal-700 text-white p-3 rounded-lg hover:bg-teal-600 focus:outline-none"
          >
            Send
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Chat;
