import React, { useEffect, useState } from "react";
import axios from "axios";

const ChatMenunggu = () => {
  const [status, setStatus] = useState("menunggu"); // Default status filter
  const [data, setData] = useState([]); // State untuk menyimpan data chat
  const [loading, setLoading] = useState(false); // State loading saat fetching data
  const [buttonLoading, setButtonLoading] = useState(null); // State untuk button loading
  const [user, setUser] = useState(null); // Simulasi data user yang sedang login

  const API_URL = "http://localhost:8000/api";

  // Fetch data chat berdasarkan status
  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_URL}/chatAdmin?status=${status}`);
      console.log("Response data:", response.data); // Debugging respons API
      setData(response.data.chats || []); // Pastikan fallback ke array kosong jika undefined
    } catch (error) {
      console.error("Error fetching data:", error);
      setData([]); // Set data sebagai array kosong jika terjadi error
    } finally {
      setLoading(false);
    }
  };
  

  // Fetch user login
  const fetchUser = async () => {
    try {
      const response = await axios.get(`${API_URL}/me`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setUser(response.data.user);
    } catch (error) {
      console.error("Failed to fetch user:", error);
    }
  };

  // Set status chat menjadi 'berlangsung'
  const setStatusToBerlangsung = async (id) => {
    setButtonLoading(id);
    try {
      await axios.put(
        `${API_URL}/chats/${id}/approve`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      fetchData(); // Refresh data setelah update
    } catch (error) {
      console.error("Gagal mengubah status chat:", error);
    } finally {
      setButtonLoading(null);
    }
  };

  // Fetch data saat komponen di-mount atau saat status berubah
  useEffect(() => {
    fetchData();
    fetchUser();
  }, [status]);

  const days = ["menunggu", "berlangsung", "berakhir"];

  return (
    <div className="flex flex-col h-screen bg-teal-100 font-sans">
      {/* Header */}
      <header className="bg-teal-200 text-teal-800 px-6 py-4 flex justify-between items-center shadow">
        <h1 className="text-xl font-bold">Admin Panel</h1>
        <div className="flex items-center">
          <span className="material-icons text-3xl mr-2">account_circle</span>
          <span>{user ? user.name : "Loading..."}</span>
        </div>
      </header>

      {/* Container */}
      <div className="flex flex-1 overflow-hidden">
        {/* Sidebar */}
        <aside className="w-48 bg-teal-700 text-white flex-shrink-0">
          <nav>
            <ul className="space-y-2 p-4">
              {["Dashboard", "Jadwal", "Pendaftar", "Keuangan", "Absen", "Logout"].map((item) => (
                <li key={item}>
                  <a href={`/${item}`} className="block px-4 py-2 rounded hover:bg-teal-600">
                    {item}
                  </a>
                </li>
              ))}
            </ul>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-1 bg-teal-50 p-6 overflow-y-auto">
          <h2 className="text-2xl font-bold mb-4">Chat</h2>

          {/* Filter Status Chat */}
          <div>
            <ul className="flex mb-4">
              {days.map((item) => (
                <li
                  key={item}
                  className={`mr-4 p-2 rounded-lg cursor-pointer ${
                    status === item ? "bg-teal-700 text-white" : "bg-teal-500"
                  }`}
                >
                  <button onClick={() => setStatus(item)}>
                    {item.charAt(0).toUpperCase() + item.slice(1)}
                  </button>
                </li>
              ))}
            </ul>
            {data?.length > 0 ? (
  data.map((row) => <div key={row.id}>{row.message}</div>)
) : (
  <p className="text-gray-500">Tidak ada data chat.</p>
)}

          </div>

          {/* Data Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {loading ? (
              <div className="col-span-3 text-center text-gray-500">Loading...</div>
            ) : data.length > 0 ? (
              data.map((row) => (
                <div key={row.id} className="bg-white p-4 rounded-lg shadow-lg border border-teal-300">
                  <h3 className="text-lg font-semibold text-teal-700">{row.pengirim}</h3>
                  <p className="text-sm text-teal-600 mb-2">{row.message}</p>
                  <button
                    onClick={() => setStatusToBerlangsung(row.id)}
                    disabled={buttonLoading === row.id || row.status === "berlangsung"}
                    className={`px-4 py-2 rounded ${
                      row.status === "berlangsung"
                        ? "bg-gray-400 text-gray-700 cursor-not-allowed"
                        : "bg-teal-700 text-white hover:bg-teal-600"
                    }`}
                  >
                    {buttonLoading === row.id ? "Loading..." : "Setujui"}
                  </button>
                </div>
              ))
            ) : (
              <div className="col-span-3 text-center text-gray-500">
                Tidak ada chat dengan status "{status}".
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default ChatMenunggu;
