import React, { useState } from 'react';
import ReactPlayer from 'react-player';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Footer from './components/Footer';

function Video() {
  const [selectedVideo, setSelectedVideo] = useState({
    title: 'Judul Utama',
    url: 'https://www.tiktok.com/@example/video/1234567890', // URL TikTok contoh
  });

  // Daftar video TikTok (gunakan URL TikTok yang valid)
  const videos = [
    { title: 'Video 1', url: 'https://www.tiktok.com/@example/video/1234567890' },
    { title: 'Video 2', url: 'https://www.tiktok.com/@example/video/1234567891' },
    { title: 'Video 3', url: 'https://www.tiktok.com/@example/video/1234567892' },
  ];

  const handleVideoClick = (video) => {
    setSelectedVideo({
      title: video.title,
      url: video.url,
    });
  };

  return (
    <div className="App">
      <Header />
      <Carousel />
      <section className="video-section">
        <h1>VIDEO</h1>
        <div className="video-grid">
          {videos.map((video, index) => (
            <div
              className="video-card"
              key={index}
              onClick={() => handleVideoClick(video)}
            >
              <div className="video-thumbnail">
              <ReactPlayer
              url={selectedVideo.url}
              width="100%"
              height="auto"
              controls={true}
            />
                <button className="play-button">â–¶</button>
              </div>
              <p>{video.title}</p>
            </div>
          ))}
        </div>

        <div className="large-video-card">
          <p>{selectedVideo.title}</p>
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default Video;
