import React, { useEffect, useState } from "react";
import api from "../api";
import axios from "axios";

const ChatMenunggu = () => {
  const [data, setData] = useState([]); // Data chat
  const [status, setStatus] = useState("menunggu"); // Status chat untuk filter
  const [loading, setLoading] = useState(false); // Loading state
  const [user, setUser] = useState(null);

  useEffect(() => {
    axios
      .get('http://localhost:8000/user')
      .then((response) => {
        setUser(response.data); // Simpan data user ke state
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching user info:', error);
        setLoading(false);
      });
  }, []);
  // Ambil data chat berdasarkan status
  useEffect(() => {
    setLoading(true);
    api
      .get(`/chatAdmin?status=${status}`)
      .then((response) => {
        setData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, [status]);

  // Update status chat menjadi "berlangsung"
  const setStatusToBerlangsung = (chatId) => {
    setLoading(true);
    api
      .put(`/chat/${chatId}`, { status: "berlangsung" })
      .then((response) => {
        // Update status chat di UI setelah berhasil
        setData(
          data.map((chat) =>
            chat.id === chatId ? { ...chat, status: "berlangsung" } : chat
          )
        );
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error updating status:", error);
        setLoading(false);
      });
  };

  return (
    <div className="flex flex-col h-screen bg-teal-100 font-sans">
      {/* Header */}
      <header className="bg-teal-200 text-teal-800 px-6 py-4 flex justify-between items-center shadow">
        <h1 className="text-xl font-bold">Admin Panel</h1>
        <div className="flex items-center">
          <span className="material-icons text-3xl mr-2">account_circle</span>
          <span>Munet</span>
        </div>
      </header>

      {/* Container */}
      <div className="flex flex-1 overflow-hidden">
        {/* Sidebar */}
        <aside className="w-48 bg-teal-700 text-white flex-shrink-0">
          <nav>
            <ul className="space-y-2 p-4">
              <li>
                <a href="/Dashboard" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Dashboard
                </a>
              </li>
              <li>
                <a href="/Jadwal" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Jadwal
                </a>
              </li>
              <li>
                <a href="/Pendaftar" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Pendaftar
                </a>
              </li>
              <li>
                <a href="/Keuangan" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Keuangan
                </a>
              </li>
              <li>
                <a href="/Absen" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Presensi
                </a>
              </li>
              <li>
                <a href="/" className="block px-4 py-2 rounded hover:bg-teal-600">
                  Logout
                </a>
              </li>
            </ul>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-1 bg-teal-50 p-6 overflow-y-auto">
          <h2 className="text-2xl font-bold mb-4">Chat</h2>

          {/* Filter Status Chat */}
          <div>
            <ul className="flex mb-4">
              <li className="mr-4 p-2 bg-teal-700 rounded-lg cursor-pointer">
                <button onClick={() => setStatus("menunggu")}>Menunggu</button>
              </li>
              <li className="mr-4 p-2 bg-teal-700 rounded-lg cursor-pointer">
                <button onClick={() => setStatus("berlangsung")}>Berlangsung</button>
              </li>
              <li className="mr-4 p-2 bg-teal-700 rounded-lg cursor-pointer">
                <button onClick={() => setStatus("berakhir")}>Berakhir</button>
              </li>
            </ul>
          </div>

          {/* Data Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {loading ? (
              <div className="col-span-3 text-center text-gray-500">
                Loading...
              </div>
            ) : data.length > 0 ? (
              data.map((row) => (
                <div key={row.id} className="bg-white p-4 rounded-lg shadow-lg border border-teal-300">
                  <h3 className="text-lg font-semibold text-teal-700">
                    {row.pengirim}
                  </h3>
                  <p className="text-sm text-teal-600 mb-2">{row.message}</p>
                  <button
                    onClick={() => setStatusToBerlangsung(row.id)}
                    className="bg-teal-700 text-white px-4 py-2 rounded hover:bg-teal-600"
                  >
                    Setujui
                  </button>
                </div>
              ))
            ) : (
              <div className="col-span-3 text-center text-gray-500">
                Tidak ada chat dengan status "{status}".
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default ChatMenunggu;
