import React, { useEffect } from "react";

function Hero() {
  const pindahHalaman = (url) => {
    window.location.href = url;
  };

  useEffect(() => {
    const handleScroll = () => {
      const elements = document.querySelectorAll(".hero .animate");
      console.log(elements); // Memastikan elemen ditemukan
      elements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        console.log(rect.top, rect.bottom); // Debugging posisi elemen
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll(); // Trigger check on initial render
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <section className="hero relative">
      <div className="overlay flex items-start gap-10 mx-24 mt-12 text-left">
        <img
          src="/images/foto.png"
          alt="Institut Teknologi Nasional"
          className="animate opacity-80 scale-90 transition duration-1000 ease-out"
        />
        <div className="isi animate mt-20">
          <h1 className="text-4xl font-bold opacity-0 transform translate-y-12 transition duration-1000 ease-out">
            Pendaftaran Telah Dibuka!
          </h1>
          <p className="mt-4 text-lg opacity-0 transform translate-y-12 transition duration-1000 ease-out">
            Pendaftaran mahasiswa baru telah dibuka, jangan lewatkan kesempatan
            untuk mendaftarkan dirimu ke kampus yang kamu impikan. Daftarkan
            dirimu sekarang sebelum ketinggalan!
          </p>
          <button
            id="openForm"
            className="register-button mt-6 px-6 py-3 bg-teal-800 text-white text-lg rounded-lg opacity-0 transform translate-y-12 transition duration-1000 ease-out"
            onClick={() => pindahHalaman("/formulir")}
          >
            Daftar
          </button>
        </div>
      </div>
    </section>
  );
}

export default Hero;
