import React, { useState } from 'react';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Footer from './components/Footer';
import ReactPlayer from 'react-player';

function Video() {
  const [selectedVideo, setSelectedVideo] = useState({
    title: 'Judul Utama',
    description: 'Deskripsi video utama',
    url: '', // URL for the TikTok video
  });

  const videos = [
    { title: 'Video 1', url: 'https://www.tiktok.com/@itnmalang.official/video/7374965570556546310?is_from_webapp=1&sender_device=pc' }, // TikTok Video URL
    { title: 'Video 2', url: 'https://www.tiktok.com/@tiktok/video/7022880818386610946' },
    { title: 'Video 3', url: 'https://www.tiktok.com/@tiktok/video/7022719349244317185' },
  ];

  const handleVideoClick = (video) => {
    setSelectedVideo({
      title: video.title,
      description: 'Deskripsi untuk ' + video.title,
      url: video.url, // Update the selected video URL
    });
  };

  return (
    <div className="App">
      <Header />
      <Carousel />
      <section className="video-section">
        <h1>VIDEO</h1>
        
        {/* Grid of video thumbnails */}
        <div className="video-grid">
          {videos.map((video, index) => (
            <div className="video-card" key={index} onClick={() => handleVideoClick(video)}>
              <div className="video-thumbnail">
                <button className="play-button">▶</button>
                {selectedVideo.url && (
            <div className="video-player">
              <ReactPlayer
                url={selectedVideo.url}
                playing
                controls
                width="100%"
              />
            </div>
          )}
              </div>
              <p>{video.title}</p>
            </div>
          ))}
        </div>

        {/* Large video display */}
        <div className="large-video-card">
          <div className="video-thumbnail">
            <button className="play-button">▶</button>
          </div>
          <p>{selectedVideo.title}</p>
          <p className="description">{selectedVideo.description}</p>

          {/* Display selected TikTok video */}
          {selectedVideo.url && (
            <div className="video-player">
              <ReactPlayer
                url={selectedVideo.url}
                playing
                controls
                width="100%"
              />
            </div>
          )}
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default Video;
