import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function Konfirmasi() {
  const [isDialogOpen, setIsDialogOpen] = useState(true); // Untuk mengatur status dialog

  // Fungsi untuk menangani konfirmasi (Yes)
  const handleConfirm = () => {
    alert('Action confirmed!'); // Bisa diganti dengan aksi lain
    setIsDialogOpen(false); // Menutup dialog setelah konfirmasi
  };

  // Fungsi untuk menutup dialog (No)
  const handleClose = () => {
    setIsDialogOpen(false); // Menutup dialog
  };

  return (
    <div className="App">
      <Header />
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <div className="p-8 rounded-lg shadow-lg w-80 text-center">
              <h2 className="text-2xl font-semibold mb-4">Welcome!</h2>
              <p className="text-gray-600 mb-6">This is a box with a button.</p>
              <button className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none">
                Click Me
              </button>
            </div>
        </div>
      <Footer />
    </div>
  );
}

export default Konfirmasi;
