import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function Konfirmasi() {
  const [isDialogOpen, setIsDialogOpen] = useState(true); // Untuk mengatur status dialog

  // Fungsi untuk menangani konfirmasi (Yes)
  const handleConfirm = () => {
    alert('Action confirmed!'); // Bisa diganti dengan aksi lain
    setIsDialogOpen(false); // Menutup dialog setelah konfirmasi
  };

  // Fungsi untuk menutup dialog (No)
  const handleClose = () => {
    setIsDialogOpen(false); // Menutup dialog
  };

  return (
    <div className="App">
      <Header />
      {isDialogOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96 text-center">
            <p className="text-lg font-medium mb-4">Are you sure you want to continue?</p>
            <div className="flex justify-around">
              <button
                onClick={handleConfirm}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none"
              >
                Yes
              </button>
              <button
                onClick={handleClose}
                className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 focus:outline-none"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
}

export default Konfirmasi;
