import React, { useEffect } from 'react';

function Hero() {
  const pindahHalaman = (url) => {
    window.location.href = url;
  };

  useEffect(() => {
    const handleScroll = () => {
      const elements = document.querySelectorAll('.animate');
      elements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight && rect.bottom > 0) {
          el.classList.add('active');
        } else {
          el.classList.remove('active');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Trigger check on initial render
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className="hero bg-white">
      <div className="flex flex-col md:flex-row items-start gap-8 mx-auto mt-12 md:mt-0 px-6 md:px-20">
        <img
          src="/images/foto.png"
          alt="Institut Teknologi Nasional"
          className="w-full md:w-2/5 opacity-80 scale-95 transition-all duration-1000 ease-out transform"
        />
        <div className="text-left">
          <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4 opacity-0 transform translate-y-12 transition-all duration-1000 ease-out">
            Pendaftaran Telah Dibuka!
          </h1>
          <p className="text-lg md:text-xl text-gray-700 mb-6 opacity-0 transform translate-y-12 transition-all duration-1000 ease-out">
            Pendaftaran mahasiswa baru telah dibuka, jangan lewatkan kesempatan untuk mendaftarkan dirimu ke kampus yang kamu impikan. Daftarkan dirimu sekarang sebelum ketinggalan!
          </p>
          <button
            id="openForm"
            className="bg-teal-700 hover:bg-teal-800 text-white font-semibold py-3 px-6 rounded-lg text-lg md:text-xl opacity-0 transform translate-y-12 transition-all duration-1000 ease-out"
            onClick={() => pindahHalaman('/formulir')}
          >
            Daftar
          </button>
        </div>
      </div>
    </section>
  );
}

export default Hero;
